// https://leetcode.com/problems/regular-expression-matching/

// dp，O(n^2)做法
class Solution {
public:
	bool isMatch(string s, string p) {
		string pp;
		bool star = false;
		for (int i = 0; i < p.size(); ++i) {
			if (p[i] == '*') {
				if (!star) {
					pp += p[i];
					star = true;
				}
			} else {
				star = false;
				pp += p[i];
			}
		}
		return helper(s, pp);
		// 对于大的那个案例，为何直接返回反而TLE了？
		// return helper(s, p);
	}

	bool helper(string s, string p) {
		int ss = s.size(), ps = p.size();
		s = ' ' + s;
		p = ' ' + p;
		vector<vector<bool> > dp(ps+1, vector<bool>(ss+1, false));
		dp[0][0] = true;

		for (int i = 1; i <= ps; ++i) {
			if (p[i] == '*') {
				dp[i][0] = dp[i-1][0];
				for (int j = 1; j <= ss; ++j)
					dp[i][j] = dp[i-1][j] || dp[i-1][j-1] || dp[i][j-1];
				continue;
			}
			for (int j = 1; j <= ss; ++j) {
				if (p[i] == '?')
					dp[i][j] = dp[i-1][j-1];
				else if (p[i] != '*')
					dp[i][j] = dp[i-1][j-1] && (p[i] == s[j]);
			}
		}

		/*for (int i = 0; i <= ps; ++i)
			for (int j = 0; j <= ss; ++j)
				printf("i=%d, j=%d, dp=%d\n", i, j, (int)dp[i][j]);
		*/
		return dp[ps][ss];
	}
};

